import React from 'react'
import Botao from './Botao'
import {Card} from 'react-bootstrap';

class Timer extends React.Component{

	constructor(props){
		super(props)
		this.state = {
            segundos: 0,
            minutos: 0,
            isOn: false,
            status: "Parado",
            label: "Iniciar"
        }
        this.handleInputChange = this.handleInputChange.bind(this);
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({
			[name]: value
        });
      }

    toggle(){
        if(this.state.isOn===true) {
			this.setState({isOn: false,status: "Parado",label:"Iniciar"})
        }
        else {
			this.setState({isOn: true,status: "Iniciando",label:"Parar"})
        }
    }

    decrementarMin(){
        this.setState({minutos: this.state.minutos -1})
    }

	decrementarSeg(){
        this.setState (
            (state) => {
                if(this.state.isOn===true){
					if(state.segundos <= 0 && state.minutos>0){
						this.segreset()
						this.decrementarMin()
					} else if(state.segundos <= 1 && state.minutos === 0){
						this.toggle()
						return({segundos: 0})
					}
					return({segundos: state.segundos - 1})
				}
            }
        )
    }

    segreset(){
        this.setState({segundos: 59})
    }

    componentDidMount(){
        this.timer = setInterval( () => this.decrementarSeg(),1000)
    }

    render(){
        const {isOn} = this.state;
        return(
            <div>
                <div class="justify-content-center d-flex">
					<h1 class="display-3">{this.state.minutos}:{this.state.segundos}</h1>
				</div>
            {isOn
				?null
				: 
				<div id="form mx-auto">
					<Card class="mb-4" className="text-center" border="primary">
						<Card.Header>
							<strong className="m-auto">Digite o tempo</strong>
						</Card.Header>
						<Card.Body class="">
							<form>
								<label>Minutos:
									<input type="number" id="min" name="minutos" value={this.state.minutos} min="0" onChange={this.handleInputChange}/>
								</label>
								<br />
								<label>Segundos:
									<input type="number" id="seg" name="segundos" value={this.state.segundos} min="0" max="59" onChange={this.handleInputChange}/>
								</label>
							</form>
						</Card.Body>
					</Card>
				</div>
			}
				<div class="justify-content-center d-flex">
					<Botao class="mt-2 mb-2" variant="primary" onClick={()=> this.toggle()} label={this.state.label}></Botao>
				</div>
            </div>
		)
    }
}

export default Timer
