import React from 'react'
import {Card} from 'react-bootstrap';

class Relogio extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            relogio1: "",
            relogio2: "",
            relogio3: "",
			relogio4: ""
        }

    }
    componentDidMount(){
        this.relogio = setInterval( () => this.relogioupdate(),1000)
    }

	relogioupdate() {
	var moment = require('moment-timezone')
	let localTime = moment( ).tz("Brasilia/Brazil").format('HH:mm:ss').toString()
	this.setState({relogio1: "Brasília, Brasil: "+localTime}) 

	localTime = moment( ).tz("/").format('HH:mm:ss').toString()
	this.setState({relogio2: "Tóquio, Japão: "+localTime})

	localTime = moment( ).tz("/").format('HH:mm:ss').toString()
	this.setState({relogio3: "Lisboa, Portugal: "+localTime})
	}

    gerarParcial(){
        let p = this.state.minutos + ":" + this.state.segundos
        this.setState({
            parcial: this.state.parcial + " || " +p
        })
    }


    render(){
        return (
            <div class="my-5">
				<Card class="mb-4" className="text-center" border="primary">
					<Card.Header>
						<strong class="display-4">Outros Locais</strong>
					</Card.Header>
					<Card.Body>
						<h3>{this.state.relogio1}</h3>
						<h3>{this.state.relogio2}</h3>
						<h3>{this.state.relogio3}</h3>
					</Card.Body>
				</Card>
            </div>

        )
    }
}

export default Relogio
