            relogio3: "",
			relogio4: ""
        }

    }
    componentDidMount(){
        this.relogio = setInterval( () => this.relogioupdate(),1000)
    }

        relogioupdate() {
        var moment = require('moment-timezone')
        let localTime = moment( ).tz("Brasilia/Brazil").format('HH:mm:ss').toString()
        this.setState({relogio1: "Brasília, Brasil: "+localTime}) 

        localTime = moment( ).tz("/").format('HH:mm:ss').toString()
        this.setState({relogio2: "Tóquio, Japão: "+localTime})

        localTime = moment( ).tz("/").format('HH:mm:ss').toString()
        this.setState({relogio3: "Lisboa, Portugal: "+localTime})
        }

    gerarParcial(){
        let p = this.state.minutos + ":" + this.state.segundos
        this.setState({
            parcial: this.state.parcial + " || " +p
        })
    }


    render(){
        return (
            <div class="my-5">
            <h1 >{this.state.relogio1}</h1>
            <h1 class="display-4">{this.state.relogio2}</h1>
            <h1 class="display-4">{this.state.relogio3}</h1>
            </div>

        )
    }
}

export default Relogio
