import React from 'react'
import Botao from './Botao'
import LabelCronometro from './LabelCronometro'

class Contador extends React.Component{

    constructor(props){
        super(props)
        this.state = {
            segundos: 0,
            minutos: 0,
            stop: false,
            btnStop: "Parar",
            parcial: "",
            fimMin: "",
            fimSeg: "",
        }
    }

    incrementar(){
        this.setState(
            (state) => {
                if(this.state.stop===false){
					if(state.segundos >= 59){
						this.zerar()
						this.incrementarMinuto()
					}
				return({segundos: state.segundos + 1})
				}
            }
        )
    }

    incrementarMinuto(){
        this.setState({minutos: this.state.minutos +1})
    }

    componentDidMount(){
        this.timer = setInterval( () => this.incrementar(),1000)
    }

	zerar(){
        this.setState({segundos: 0})
    }

    zerarCronometro(){
        this.setState({
            segundos: 0,
            minutos: 0,
            parcial: "",
            fimMin:"",
            fimSeg:""
        })
    }

    pararCronometro(){
        this.setState({
            stop: !this.state.stop
        })
        if(this.state.stop)
			this.setState({
				btnStop: "Parar"
			})
        else 
			this.setState({
				btnStop: "Continuar"
            })
    }

    obterParcial(){
        let p = this.state.minutos + ":" + this.state.segundos
        let duraMin = this.state.minutos - this.state.fimMin
        let duraSeg = this.state.segundos - this.state.fimSeg
        this.setState({
            fimMin: this.state.minutos,
            fimSeg: this.state.segundos,
            parcial: this.state.parcial +"\n"+p+" || Diferença: "+duraMin+":"+duraSeg
        })
    }

    render(){
        return (
            <div>
                <div class="justify-content-center d-flex">
					<h3 class='mx-auto display-4'>{this.state.minutos}:{this.state.segundos}</h3>
				</div>
				<div class="d-flex justify-content-center">
					<Botao variant="primary" onClick={()=> this.zerarCronometro()} label="Zerar" />
					<Botao variant="primary" onClick={()=> this.pararCronometro()} label={this.state.btnStop} />
					<Botao variant="primary" onClick={()=> this.obterParcial()} label="Parcial" />
				</div>
				<div class="parcial">
					<LabelCronometro name={this.state.parcial}/>
				</div>
            </div>
        )
    }
}

export default Contador
