import React from 'react'
import {Card} from 'react-bootstrap';
import {ListGroup} from 'react-bootstrap';

class Relogio extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            relogio1: "",
            relogio2: "",
            relogio3: "",
			relogio4: ""
        }

    }
    componentDidMount(){
        this.relogio = setInterval( () => this.relogioupdate(),1000)
    }

	relogioupdate() {
	var moment = require('moment-timezone')
	let localTime = moment( ).tz("Brasília/Brazil").format('HH:mm:ss').toString()
	this.setState({relogio1: localTime}) 

	localTime = moment( ).tz("/").format('HH:mm:ss').toString()
	this.setState({relogio2: "Tóquio, Japão: "+localTime})

	localTime = moment( ).tz("/").format('HH:mm:ss').toString()
	this.setState({relogio3: "França, Paris: "+localTime})

	localTime = moment( ).tz("/").format('HH:mm:ss').toString()
	this.setState({relogio4: "Lisboa, Portugal: "+localTime})
	}

    gerarParcial(){
        let p = this.state.minutos + ":" + this.state.segundos
        this.setState({
            parcial: this.state.parcial + " || " +p
        })
    }


    render(){
        return (
            <div class="my-5">
				<h5 class="mb-4">{this.state.relogio1}</h5>
				<Card className="text-center" border="primary">
					<Card.Header>
						<strong>Outros Locais</strong>
					</Card.Header>
					<ListGroup variant="flush">
						<ListGroup.Item><h5>{this.state.relogio2}</h5></ListGroup.Item>
						<ListGroup.Item><h5>{this.state.relogio3}</h5></ListGroup.Item>
						<ListGroup.Item><h5>{this.state.relogio4}</h5></ListGroup.Item>
					</ListGroup>
				</Card>
            </div>
        )
    }
}

export default Relogio
